mapper.update:
		UPDATE acm  
    		SET acm_nm = #{acmNm}
    		, acm_se = #{acmSe}
    		, acm_addr = #{Addr}
    		, acm_pri = #{Price}
    		, acm_qty = #{Qty}
    		, acm_rd = sysdate
    		, acm_wrt = #{Writer}
    		, atch_file_id = #{atchFileId}
		WHERE             
    		acm_id = #{acmId}

mapper.select:
	select * from acm where 1 = 1

 		and acm_id = #{acmId}


	 	and acm_nm = #{acmNm}


	  	and acm_se = #{acmSe}


	  	and acm_addr like '%' || #{Addr} || '%'


	  	and acm_pri like '%' || #{Price} || '%'


	  	and acm_qty like '%' || #{Qty} || '%'


	  	and acm_rd like '%' || #{acmRd} || '%'


	  	and acm_wrt like '%' || #{Writer} || '%'


	  	and atch_file_id like '%' || #{atchFileId} || '%'
